<style lang="scss">
	@use "sass:math";
	@import '../styles/utils';

	.section.hero {
		$overlay-min-opacity: 0.1;
		$overlay-max-opacity: 0.25;
		scroll-margin-top: 0 !important;
		scroll-margin: 0 !important;
		scroll-padding-top: 0 !important;
		display: flex;
		flex-direction: column;
		min-height: 95vh;
		background: linear-gradient(
				to bottom,
				rgba(#fff, $overlay-min-opacity),
				rgba(#fff, $overlay-max-opacity)
			),
			url(/images/hero--mobile.jpg) no-repeat top center/cover fixed;
		color: #fff;
		box-sizing: border-box;
		text-align: center;
		@include from-portrait {
			min-height: 100vh;
			padding-top: $vflow * 4;
		}
		@include from-landscape {
			background-image: linear-gradient(
					to bottom,
					rgba(#fff, $overlay-min-opacity),
					rgba(#fff, $overlay-max-opacity)
				),
				url(/images/hero--desktop.jpg);
		}
	}

	.hero__title {
		font-family: 'Cormorant Garamond', serif;
		font-size: 3.5rem;
		font-weight: normal;
		line-height: 1.3;
		color: white;
		text-shadow: -2px 2px 4px black;
		@include from-portrait {
			font-size: 4rem;
		}
	}

	.features {
		flex-grow: 1;
		padding: $vflow;
		@include from-landscape {
		}
	}

	.features__list {
		@extend %list-reset;
		display: flex;
		flex-wrap: wrap;
		justify-content: center;
		align-items: flex-start;
	}

	.feature {
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		opacity: 0.95;
		padding: 0 0.1rem 1.5rem;
		flex: 1 33.333%;
	}

	.feature__circle {
		$icon-size: Rem(75px);
		display: flex;
		width: $icon-size;
		height: $icon-size;
		border: 2px solid $black;
		padding: 0.7rem;
		border-radius: 50%;
		margin-bottom: Rem(8px);
		justify-content: center;
		align-items: center;
		background-color: rgba(#fff, 0.85);
	}

	.feature__icon {
		width: 100%;
		height: 100%;
	}

	.feature__label {
		text-align: center;
		text-transform: uppercase;
		font-size: Rem(15px);
		color: white;
		text-shadow: -2px 2px 4px black;
	}

	.hero-cta {
		margin-top: $vflow;
	}
</style>
